import { useState } from 'react';
import './App.css';

function App() {
  const [imgSrc, setImgSrc] = useState(null);

  const fileReader = (event) => {
    console.log('event: ', event);
    const files = Array.from(event.target.files);
    if (files && files[0]) {
      console.log('files', files);
      setImgSrc(URL.createObjectURL(files[0]));
    }
  };

  return (
    <div className="App">
      <header>
        Image Sharing
      </header>

      <div className="main">

        <p>
          Drag an image to upload or click below
      </p>
        {/* <button className="upload-button" onClick={fileReader}>
          Upload
        </button> */}
        <input type='file' onChange={fileReader} />
      </div>
      <div className={"image-container" + (!imgSrc ? " hidden" : "")}>
        <p>Nice picture!</p>
        <img className="img" src={imgSrc} alt="uploaded image" />
      </div>
    </div>
  );
}

export default App;
